package org.netbeans.test.junit.testresults.test;

import junit.framework.TestCase;
import junit.framework.*;

/**
 *
 * @author ms159439
 */
public class ResultTestSuite extends TestCase {
    
    public ResultTestSuite(String testName) {
        super(testName);
    }

    protected void setUp() throws Exception {
    }

    protected void tearDown() throws Exception {
    }

    /**
     * suite method automatically generated by JUnit module
     */
    public static Test suite() {
        junit.framework.TestSuite suite = new junit.framework.TestSuite("TestSuite");
        suite.addTest(TestClassTest.suite());
        suite.addTest(TestSuccessTest.suite());
        return suite;
    }
    
}
