private Integer nextPK() throws java.sql.SQLException {
    // ~PrimaryKeyMethod3~
        com.sun.sql.rowset.CachedRowSetXImpl pkRowSet = new com.sun.sql.rowset.CachedRowSetXImpl();
        try {
        // ~PrimaryKeyMethod4~
            pkRowSet.setDataSourceName("java:comp/env/jdbc/Travel");
        // ~PrimaryKeyMethod5~
            pkRowSet.setCommand("SELECT MAX(TRAVEL.PERSON.PERSONID) + 1 FROM TRAVEL.PERSON");
        pkRowSet.setTableName("TRAVEL.PERSON");
        // ~PrimaryKeyMethod6~
        pkRowSet.execute();    
        pkRowSet.next();
        // ~PrimaryKeyMethod7~
        int counter = pkRowSet.getInt(1);
        log ("New PK is :" + counter);
        return new Integer(counter);
        }catch(Exception e) {
            error("Error fetching Max(PERSONID)+1 : " + e.getMessage());
        }finally {
        pkRowSet.close();
        }
        return null;
}
