// ~GetCookie1~
var allcookies = document.cookie;
// ~GetCookie2~
var pos = allcookies.indexOf("version=");

// ~GetCookie3~
if (pos != -1) {
    var start = pos + 8;                          // ~GetCookie4~ 
    var end = allcookies.indexOf(";", start);     // ~GetCookie5~
    if (end == -1) end = allcookies.length;
    var value = allcookies.substring(start, end); // ~GetCookie6~
    value = unescape(value)                       // ~GetCookie7~

    // ~GetCookie8~
    // ~GetCookie9~
    // ~GetCookie10~
    // ~GetCookie11~
    if (value != document.lastModified)
           document.write(document.cookie);
           document.write("value is" + value);
           alert("This doucment has changed since you were last here");
}
