codeclip_palette_item.body:

        // ~SendEmail1~
        // ~SendEmail2~
        // ~SendEmail3~
        // ~SendEmail4~
        // ~SendEmail5~
        // ~SendEmail6~
        // ~SendEmail7~
        String to = "@~Param_To_Address~@";     // ~SendEmail8~
        String from = "@~Param_From_Address~@"; // ~SendEmail9~
        String subject = "@~Param_Subject~@";   // ~SendEmail10~
        String message = null;
        String mailhost = "@~Param_SMTP_mail_server_name~@"; // ~SendEmail11~
        String user = "@~Param_User_name~@";                 // ~SendEmail12~
        String password = "@~Param_Password~@";              // ~SendEmail13~
		// ~SendEmail14~
        boolean auth = true;
        boolean ssl = false;
        Properties props = System.getProperties();

        if (mailhost != null)
            props.put("mail.smtp.host", mailhost);
        if (auth)
            props.put("mail.smtp.auth", "true");

        // Get a Session object
        javax.mail.Session session = javax.mail.Session.getInstance(props, null);

        // construct the message
        javax.mail.Message msg = new MimeMessage(session);

        try {
            //  Set message details
            msg.setFrom(new InternetAddress(from));
            msg.setRecipient(javax.mail.Message.RecipientType.TO, new InternetAddress(to));
            msg.setSubject(subject);
            msg.setSentDate(new Date());
            msg.setText(message);

            // send the thing off
            SMTPTransport t =
                    (SMTPTransport)session.getTransport(ssl ? "smtps" : "smtp");
            try {
                if (auth)
                    t.connect(mailhost, user, password);
                else
                    t.connect();
                t.sendMessage(msg, msg.getAllRecipients());
            } finally {
                t.close();
            }
            log("~Log_Success~");

        } catch (Exception e) {
            if (e instanceof SendFailedException) {
                MessagingException sfe = (MessagingException)e;
                if (sfe instanceof SMTPSendFailedException) {
                    SMTPSendFailedException ssfe =
                            (SMTPSendFailedException)sfe;
                    log("SMTP SEND FAILED:");
                }
                Exception ne;
				while ((ne = sfe.getNextException()) != null &&
					ne instanceof MessagingException) 	{
		    sfe = (MessagingException)ne;
		    if (sfe instanceof SMTPAddressFailedException) {
			SMTPAddressFailedException ssfe =
					(SMTPAddressFailedException)sfe;
			log("ADDRESS FAILED:");
			log(ssfe.toString());
			log("  Address: " + ssfe.getAddress());
			log("  Command: " + ssfe.getCommand());
			log("  RetCode: " + ssfe.getReturnCode());
			log("  Response: " + ssfe.getMessage());
		    } else if (sfe instanceof SMTPAddressSucceededException) {
			log("ADDRESS SUCCEEDED:");
			SMTPAddressSucceededException ssfe =
					(SMTPAddressSucceededException)sfe;
		    }
				}
	    } else {
			log("~Log_Exception~: " + e);	   
          }
        }
